import express from 'express';
import session from 'express-session';
import MongoStore from 'connect-mongo';
import cors from 'cors';
import dotenv from 'dotenv';
import connectDB from './config/database.js';
import apiRoutes from './routes/api.js';
import authRoutes from './routes/auth.js';
import path from 'path';
import { fileURLToPath } from 'url';

dotenv.config();

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const app = express();

// Conectar ao MongoDB
connectDB();

// Middleware
app.use(cors());
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// Configurar sess√£o
app.use(session({
  secret: process.env.SESSION_SECRET || 'supercartola-secret-key',
  resave: false,
  saveUninitialized: false,
  store: MongoStore.create({
    mongoUrl: process.env.MONGODB_URI,
    collectionName: 'sessions'
  }),
  cookie: {
    secure: false, // Em produ√ß√£o com HTTPS, mudar para true
    maxAge: 7 * 24 * 60 * 60 * 1000 // 7 dias
  }
}));

// Arquivos est√°ticos
app.use(express.static(path.join(__dirname, 'public')));

// Rotas
app.use('/auth', authRoutes);
app.use('/api', apiRoutes);

// Rota principal
app.get('/', (req, res) => {
  res.redirect('/participante/');
});

// Rota admin
app.get('/admin', (req, res) => {
  res.redirect('/admin/');
});

// Iniciar servidor
const PORT = process.env.PORT || 3000;
app.listen(PORT, '0.0.0.0', () => {
  console.log(`üöÄ Servidor rodando na porta ${PORT}`);
  console.log(`üì± App Participante: http://localhost:${PORT}/participante/`);
  console.log(`üîß Admin: http://localhost:${PORT}/admin/`);
});

// Tratamento de erros globais
process.on('uncaughtException', (err) => {
  console.error('Erro n√£o tratado:', err);
});

process.on('unhandledRejection', (err) => {
  console.error('Promessa rejeitada n√£o tratada:', err);
});

export default app;
</result>

